const fs = require('fs');
const path = require('path');

// Ensure directories exist
const directories = [
  'categories',
  'general',
  'icons'
];

directories.forEach(dir => {
  const dirPath = path.join(__dirname, dir);
  if (!fs.existsSync(dirPath)) {
    fs.mkdirSync(dirPath, { recursive: true });
    console.log(`Created directory: ${dir}`);
  }
});

// Create base64 placeholder images for categories
const categoryPlaceholders = {
  'birthday.jpg': 'data:image/svg+xml;base64,',
  'anniversary.jpg': 'data:image/svg+xml;base64,',
  'wedding.jpg': 'data:image/svg+xml;base64,',
  'sympathy.jpg': 'data:image/svg+xml;base64,',
};

// Create images in categories folder
Object.entries(categoryPlaceholders).forEach(([filename, base64Data]) => {
  const filePath = path.join(__dirname, 'categories', filename);
  const fileData = base64Data.replace(/^data:image\/\w+;base64,/, '');
  fs.writeFileSync(filePath, Buffer.from(fileData, 'base64'));
  console.log(`Created category placeholder: ${filename}`);
});

// Create general logo placeholder
const logoPlaceholder = 'data:image/svg+xml;base64,';
fs.writeFileSync(path.join(__dirname, 'general', 'Logo.png'), Buffer.from(logoPlaceholder.replace(/^data:image\/\w+;base64,/, ''), 'base64'));
console.log('Created general logo placeholder');

// Create icon placeholder
const googleIconPlaceholder = 'data:image/svg+xml;base64,';
fs.writeFileSync(path.join(__dirname, 'icons', 'google-logo.png'), Buffer.from(googleIconPlaceholder.replace(/^data:image\/\w+;base64,/, ''), 'base64'));
console.log('Created Google icon placeholder');

// Create hero banner placeholder 
const heroBannerPlaceholder = 'data:image/svg+xml;base64,';
fs.writeFileSync(path.join(__dirname, 'hero-banner.jpg'), Buffer.from(heroBannerPlaceholder.replace(/^data:image\/\w+;base64,/, ''), 'base64'));
console.log('Created hero banner placeholder');

console.log('All placeholders created successfully!');